import db from '../models/';

const postController = {};

postController.post = (req, res) => {
    const { title, text, link, userId } = req.body;
    const post = new db.Post({
        title,
        text,
        link,
        _creator: userId,
    });

    post.save().then((newPost) => {
        return res.status(200).json({
            success: true,
            data: newPost,
        });
    }).catch((err) => {
        return res.status(500).json({
            message: err
        });
    });
};

postController.getAll = (req, res) => {
    db.Post.find({})
        .populate({
            path: '_creator',
            select: 'username createdAt -_id'
        })
        .populate({
            path: '_comments',
            select: 'text createdAt _creator',
            match: { 'isDeleted': false }
        })
        .then((posts) => {
            return res.status(200).json({
                success: true,
                data: posts,
            });
        }).catch((err) => {
        return res.status(500).json({
            message: err
        });
    });
};

postController.vote = (req, res) => {
    const { userId, postId, isUpVoted } = req.body;
    const vote = new db.Vote({
        userId,
        post: postId,
        isUpVoted,
    });

    vote.save().then((newVote) => {
        return res.status(200).json({
            success: true,
            data: newVote,
        });
    }).catch((err) => {
        return res.status(500).json({
            message: err
        });
    });
};

export default postController;

