sourcesContent:
import db from '../models/index';

const countController = {};

// Function to count upvotes and downvotes for a post
countController.countVotes = (req, res) => {
    const postId = req.params.postId;

    db.Vote.aggregate([
        { $match: { postId: db.mongoose.Types.ObjectId(postId) } },
        {
            $group: {
                _id: "$isUpVoted",
                count: { $sum: 1 }
            }
        }
    ]).then((result) => {
        let upVotes = 0;
        let downVotes = 0;

        result.forEach((vote) => {
            if (vote._id === true) {
                upVotes = vote.count;
            } else {
                downVotes = vote.count;
            }
        });

        res.status(200).json({
            success: true,
            data: {
                upVotes,
                downVotes
            }
        });
    }).catch((err) => {
        res.status(500).json({
            message: err.message || "Error counting votes."
        });
    });
};

countController.countCommentVotes = (req, res) => {
    const commentId = req.params.commentId;

    db.Vote.aggregate([
        { $match: { commentId: db.mongoose.Types.ObjectId(commentId) } },
        {
            $group: {
                _id: "$isUpVoted",
                count: { $sum: 1 }
            }
        }
    ]).then((result) => {
        let upVotes = 0;
        let downVotes = 0;

        result.forEach((vote) => {
            if (vote._id === true) {
                upVotes = vote.count;
            } else {
                downVotes = vote.count;
            }
        });

        res.status(200).json({
            success: true,
            data: {
                upVotes,
                downVotes
            }
        });
    }).catch((err) => {
        res.status(500).json({
            message: err.message || "Error counting comment votes."
        });
    });
};


export default countController;

