import db from '../models/index';

const countController = {};

// Ensure mongoose is properly initialized
const mongoose = db.mongoose;

// Function to calculate the net count of votes for a post
countController.calculatePostVoteCount = (postId) => {
    return new Promise((resolve, reject) => {
        db.Vote.aggregate([
            { $match: { postId: mongoose.Types.ObjectId(postId) } }, // Ensure postId is converted to ObjectId
            {
                $group: {
                    _id: null,
                    totalVotes: { $sum: { $cond: { if: "$isUpVoted", then: 1, else: -1 } } }
                }
            }
        ]).exec((err, result) => {
            if (err) {
                reject(err);
            } else {
                resolve(result.length > 0 ? result[0].totalVotes : 0);
            }
        });
    });
};

// Function to calculate the net count of votes for a comment
countController.calculateCommentVoteCount = (commentId) => {
    return new Promise((resolve, reject) => {
        db.Vote.aggregate([
            { $match: { commentId: mongoose.Types.ObjectId(commentId) } }, // Ensure commentId is converted to ObjectId
            {
                $group: {
                    _id: null,
                    totalVotes: { $sum: { $cond: { if: "$isUpVoted", then: 1, else: -1 } } }
                }
            }
        ]).exec((err, result) => {
            if (err) {
                reject(err);
            } else {
                resolve(result.length > 0 ? result[0].totalVotes : 0);
            }
        });
    });
};

export default countController;

