sourcesContent:
import db from '../models/index';
import mongoose from 'mongoose';

// Debugging statements
console.log(db);
console.log(db.mongoose);
console.log(db.mongoose.Types);
console.log(postId);


const countController = {};

// Function to count upVotes and downVotes for a post
countController.countVotes = (req, res) => {
    const postId = req.params.postId;

    db.Vote.aggregate([
        { $match: { postId: db.mongoose.Types.ObjectId(postId) } },
        {
            $group: {
                _id: "$isUpVote",
                count: { $sum: 1 }
            }
        }
    ]).then((result) => {
        let upVotes = 0;
        let downVotes = 0;

        result.forEach((vote) => {
            if (vote._id === true) {
                upVotes += vote.count;  // Accumulate upVotes
            } else {
                downVotes += vote.count;  // Accumulate downVotes
            }
        });

        // Calculate net votes
        const netVotes = upVotes - downVotes;

        res.status(200).json({
            success: true,
            data: {
                upVotes,
                downVotes,
                netVotes
            }
        });
    }).catch((err) => {
        res.status(500).json({
            message: err.message || "Error counting votes."
        });
    });
};

// Function to count upVotes and downVotes for a comment
countController.countCommentVotes = (req, res) => {
    const commentId = req.params.commentId;

    db.Vote.aggregate([
        { $match: { commentId: mongoose.Types.ObjectId(commentId) } },
        {
            $group: {
                _id: "$isUpVote",
                count: { $sum: 1 }
            }
        }
    ]).then((result) => {
        let upVotes = 0;
        let downVotes = 0;

        result.forEach((vote) => {
            if (vote._id === true) {
                upVotes += vote.count;  // Accumulate upVotes
            } else {
                downVotes += vote.count;  // Accumulate downVotes
            }
        });

        // Calculate net votes
        const netVotes = upVotes - downVotes;

        res.status(200).json({
            success: true,
            data: {
                upVotes,
                downVotes,
                netVotes
            }
        });
    }).catch((err) => {
        res.status(500).json({
            message: err.message || "Error counting comment votes."
        });
    });
};

export default countController;
